import React, { useEffect } from "react";

import "./styles.css"

import TagCloud from "TagCloud";

const TextShpere = () => {
    // Animation settings for Text Cloud
    useEffect(() => {
      return () => {
        const container = ".tagcloud";
        const texts = [
          "HTML",
          "CSS",
          "SCSS",
          "JavaScript",
          "React",
          "Vue",
          "Nuxt",
          "NodeJS",
          "Babel",
          "Jquery",
          "Tilda",
          "Bitrix",
          "MOBX",
          "PHP",
          "ES6",
          "GIT",
          "GITHUB",
        ];
  
        const options = {
          radius: 300,
          maxSpeed: "normal",
          initSpeed: "normal",
          keep: true,
        };
  
        TagCloud(container, texts, options);
      }
    }, []);
  
    return (
      <>
      </>
    );
  };
  
  export default TextShpere;
