import {NavLink} from "react-router-dom"

import "./style.css";


function Header () {
    window.addEventListener('scroll', function() {
        let lastScrollY = window.pageYOffset;

        if (lastScrollY > 50) {
          document.querySelector('.header').classList.add('header-active')
        } else {
          document.querySelector('.header').classList.remove('header-active')
        }
    })

    // function checkCheckbox() {

    //     const checkbox = document.getElementById('myCheckbox');

    //     if (checkbox.checked) {
    //       console.log('Открыть меню');
    //       document.querySelector('.menu').classList.remove('menu-active');
    //     } else {
    //       console.log('Закрыть меню');
    //       document.querySelector('.menu').classList.add('menu-active');
    //     }
    // }

    // function closeNav() {

    //     const checkbox = document.getElementById('myCheckbox');

    //     document.querySelector('.menu').classList.remove('menu-active');
    //     checkbox.checked = false;
    // }

    return (
        <header className="header">
            <nav className="header-nav">
                <NavLink to="/" className="header-link">
                    <span className="link-num">01.</span>
                    Главная
                </NavLink>
                <NavLink to="/about" className="header-link">
                    <span className="link-num">02.</span>
                    Обо мне
                </NavLink>
                <NavLink to="/work" className="header-link">
                    <span className="link-num">03.</span>
                    Проекты
                </NavLink>
                <NavLink to="/contact" className="header-link">
                    <span className="link-num">04.</span>
                    Контакты
                </NavLink>
            </nav>
        </header>
    );
}
 
export default Header;
